import React, { useState } from "react";

const DynamicForm = () => {
  const [fields, setFields] = useState([{ name: "", email: "" }]);

  const performerChange = (index, event) => {
    const values = [...fields];
    values[index][event.target.name] = event.target.value;
    setFields(values);
  };

  const performerAddField = () => {
    setFields([...fields, { name: "", email: "" }]);
  };

  const performerRemoveField = (index) => {
    const values = [...fields];
    values.splice(index, 1);
    setFields(values);
  };

  const judgeChange = (index, event) => {
    const values = [...fields];
    values[index][event.target.name] = event.target.value;
    setFields(values);
  };

  const judgeAddField = () => {
    setFields([...fields, { name: "", email: "" }]);
  };

  const judgeRemoveField = (index) => {
    const values = [...fields];
    values.splice(index, 1);
    setFields(values);
  };

  const formSubmit = (event) => {
    event.preventDefault();
    console.log(fields);
  };

  return (
    <div className="container mt-5">
      <h2>Performers</h2>
      <form onSubmit={formSubmit}>
        {fields.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(event) => performerChange(index, event)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(event) => performerChange(index, event)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => performerRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={performerAddField}
        >
          Add Performer
        </button>
        <p />

        <h2>Judges</h2>
        {fields.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(event) => judgeChange(index, event)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(event) => judgeChange(index, event)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => judgeRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={judgeAddField}
        >
          Add Judge
        </button>

        <p />
        <button type="submit" className="btn btn-success mt-3">
          Submit
        </button>
      </form>
    </div>
  );
};

export default DynamicForm;
