import React, { useState, useEffect } from "react";
import axios from "axios";
import { type } from "@testing-library/user-event/dist/type";

const CreateEventForm = () => {
  const [judges, setJudges] = useState([
    { judge_name: "", judge_email: "", judge_weightage: 1 },
  ]);
  const [performers, setPerformers] = useState([
    { performer_name: "", performer_score: 0 },
  ]);
  const [item, setItem] = useState({ event_name: "" });
  const [categories, setCategories] = useState([{ category_name: "" }]);

  const performerChange = (index, e) => {
    const values = [...performers];
    values[index][e.target.name] = e.target.value;
    setPerformers(values);
  };
  const performerAddField = () => {
    setPerformers([...performers, { performer_name: "", performer_score: 0 }]);
  };
  const performerRemoveField = (index) => {
    const values = [...performers];
    values.splice(index, 1);
    setPerformers(values);
  };

  const judgeChange = (index, e) => {
    const values = [...judges];
    values[index][e.target.name] = e.target.value;
    setJudges(values);
  };
  const judgeAddField = () => {
    setJudges([
      ...judges,
      { judge_name: "", judge_email: "", judge_weightage: 1 },
    ]);
  };
  const judgeRemoveField = (index) => {
    const values = [...judges];
    values.splice(index, 1);
    setJudges(values);
  };

  const categoryAddField = () => {
    setCategories([...categories, { category_name: "" }]);
  };
  const categoryRemoveField = (index) => {
    const values = [...categories];
    values.splice(index, 1);
    setCategories(values);
  };

  const categoryChange = (index, e) => {
    const values = [...categories];
    values[index][e.target.name] = e.target.value;
    setCategories(values);
  };

  const eventChange = (e) => {
    const value = item;
    value[e.target.name] = e.target.value;
    setItem(value);
  };

  const val = {
    event: item,
    judges: judges,
    performers: performers,
    categories: categories,
  };

  const req = JSON.stringify(val);

  const [message, setMessage] = useState("");

  let timer = undefined;

  const updateProgress = (id, message) => {
    return new Promise((resolve) => {
      timer = setTimeout(async () => {
        await fetch(
          "http://localhost:8080/event/update/status?event_id=" +
            id +
            "&progress=" +
            message,
          {
            method: "PUT",
          }
        );
        resolve(); // Resolve the promise after the fetch completes
      }, 10000);
    });
  };

  const updatePerformers = () => {};

  const formSubmit = async (e) => {
    e.preventDefault();
    try {
      const response1 = await axios.post(
        "http://localhost:8080/admin/create",
        req,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      setMessage("Event Created Successfully!!");
      const id = response1.data;
      await updateProgress(id, "Event in Progress!!");
      console.log("after update");
      updatePerformers();
    } catch (error) {
      setMessage("Error: " + error);
    }
  };

  useEffect(() => {
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="container mt-5">
      <form onSubmit={formSubmit}>
        <h2>Event</h2>
        <div className="col">
          <input
            type="text"
            name="event_name"
            className="form-control"
            placeholder="event name"
            value={item.name}
            onChange={(e) => eventChange(e)}
            required
          />
        </div>
        <p />
        <h2>Performers</h2>
        {performers.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="performer_name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(e) => performerChange(index, e)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="performer_email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(e) => performerChange(index, e)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => performerRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={performerAddField}
        >
          Add Performer
        </button>
        <p />

        <h2>Judges</h2>
        {judges.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="judge_name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(e) => judgeChange(index, e)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="judge_email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(e) => judgeChange(index, e)}
                required
              />
            </div>
            <div className="col">
              <input
                type="number"
                name="judge_weightage"
                className="form-control"
                placeholder="Weightage(example: 1)"
                value={field.email}
                onChange={(e) => judgeChange(index, e)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => judgeRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={judgeAddField}
        >
          Add Judge
        </button>
        <p />

        <h2>Categories</h2>
        {categories.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="category_name"
                className="form-control"
                placeholder="category name"
                value={field.name}
                onChange={(e) => categoryChange(index, e)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => categoryRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={categoryAddField}
        >
          Add Category
        </button>
        <p />

        <button type="submit" className="btn btn-success mt-3">
          Submit
        </button>
      </form>
      {message != "" ? <h1>{message}</h1> : null}
    </div>
  );
};

export default CreateEventForm;
