import React, { useEffect, useState, useParams } from "react";

function DisplayCategoryForm({ categories }) {
  const [catValues, setCatValues] = useState([]);

  categories.map((cat) => {
    setCatValues([
      ...catValues,
      { category_name: cat.categoryName, category_score: 0 },
    ]);
  });

  const categoryChange = (index, e) => {
    const values = [...catValues];
    values[index][e.target.name] = e.target.value;
    setCatValues(values);
  };

  const formSubmit = (e) => {
    e.preventDefault();
  };
  const options = [1, 2, 3, 4, 5];

  return (
    <>
      <div className="container mt-5">
        <h2 className="mb-4">Score</h2>
        <form onSubmit={formSubmit}>
          <div className="form-group">
            {categories.map((cat, idx) => {
              <>
                <label key={idx} htmlFor="dropdown">
                  {cat.categoryName}
                </label>
                <select
                  name="category_name"
                  className="form-control"
                  value={catValues[idx][cat.categoryName]}
                  onChange={categoryChange}
                >
                  <option value="">--select a score--</option>
                  {options.map((option) => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </>;
            })}
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </>
  );
}

export default function ScoringPageComponent({ id }) {
  const [item, setItem] = useState();
  function fetchData() {
    fetch("http://localhost:8080/event?event_id=" + id)
      .then((response) => response.json())
      .then((data) => setItem(data));
    //console.log(item);
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      let s = "Event ended!";
      fetchData();
      if (item) {
        if (s.localeCompare(item.event.eventStatus) === 0) {
          clearInterval(intervalId);
        }
      }
    }, 1000);
  }, []);

  if (!item) return <h2>Loading...</h2>;
  if (item.event.performer_id === -1)
    return <h2>Event will start in a few minutes! Be ready to score.</h2>;
  if (item.event.performing === false)
    return (
      <>
        {item.performers
          .filter(
            (performer) => performer.performerId === item.event.performer_id
          )
          .map((item) => (
            <h3>Score {item.performerName} performance in these catagories</h3>
          ))}
        <DisplayCategoryForm />
      </>
    );
  return (
    <>
      {item.performers
        .filter(
          (performer) => performer.performerId === item.event.performer_id
        )
        .map((item) => (
          <h3>{item.performerName} performing.......</h3>
        ))}
    </>
  );
}
