import React, { useEffect, useState, useParams } from "react";

export default function DisplayCategoryForm({ categories }) {
  const [catValues, setCatValues] = useState([]);

  console.log(catValues);

  const categoryChange = (index, e) => {
    const values = [...catValues];
    values[index] = e.target.value;
    setCatValues(values);
  };

  const formSubmit = (e) => {
    e.preventDefault();
  };
  const options = [1, 2, 3, 4, 5];

  return (
    <>
      <div className="container mt-5">
        <h2 className="mb-4">Score</h2>
        <form onSubmit={formSubmit}>
          <div className="form-group">
            {categories.map((cat, idx) => {
              <>
                <label key={idx} htmlFor="dropdown">
                  {cat.categoryName}
                </label>
                <select
                  name="category_name"
                  className="form-control"
                  value={catValues[idx]}
                  onChange={categoryChange}
                >
                  <option value="">--select a score--</option>
                  {options.map((option) => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </>;
            })}
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </>
  );
}
