export default function ScoringPageComponent({ id }) {
  const [item, setItem] = useState();
  function fetchData() {
    fetch("http://localhost:8080/event?event_id=" + id)
      .then((response) => response.json())
      .then((data) => setItem(data));
    //console.log(item);
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      let s = "Event ended!";
      fetchData();
      if (item) {
        if (s.localeCompare(item.event.eventStatus) === 0) {
          clearInterval(intervalId);
        }
      }
    }, 1000);
  }, []);

  if (!item) return <h2>Loading...</h2>;
  if (item.event.performer_id === -1)
    return <h2>Event will start in a few minutes! Be ready to score.</h2>;
  if (item.event.performing === false)
    return (
      <>
        {item.performers
          .filter(
            (performer) => performer.performerId === item.event.performer_id
          )
          .map((item) => (
            <h3>Score {item.performerName} performance in these catagories</h3>
          ))}
        <DisplayCategoryForm categories={item.categories} />
      </>
    );
  return (
    <>
      {item.performers
        .filter(
          (performer) => performer.performerId === item.event.performer_id
        )
        .map((item) => (
          <h3>{item.performerName} performing.......</h3>
        ))}
    </>
  );
}
