import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function DisplayScores({ scores, id }) {
  return (
    <>
      {scores
        .filter((score) => {
          score.performers.performerId == id ? (
            <h4>Scores for {score.performs.performerName}</h4>
          ) : null;
          return score.performers.performerId == id;
        })
        .map((score, idx) => {
          <h5>
            {score.judges.judgeName} : {score.score}
          </h5>;
        })}
    </>
  );
}

function DisplayPerformers({ performers }) {
  return (
    <>
      <h3>Performers</h3>
      <table
        style={{
          width: "100%",
          border: "1px solid black",
          borderCollapse: "collapse",
        }}
      >
        <thead>
          <tr>
            <th style={{ border: "1px solid black", padding: "8px" }}>Name</th>
            <th style={{ border: "1px solid black", padding: "8px" }}>Score</th>
          </tr>
        </thead>
        <tbody>
          {performers.map((performer) => (
            <tr key={performer.performerId}>
              <td style={{ border: "1px solid black", padding: "8px" }}>
                {performer.performerName}
              </td>
              <td style={{ border: "1px solid black", padding: "8px" }}>
                {performer.performerScore}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

function DisplayJudges({ judges }) {
  return (
    <>
      <h3>Judges</h3>
      <table
        style={{
          width: "100%",
          border: "1px solid black",
          borderCollapse: "collapse",
        }}
      >
        <thead>
          <tr>
            <th style={{ border: "1px solid black", padding: "8px" }}>Name</th>
            <th style={{ border: "1px solid black", padding: "8px" }}>
              Weightage
            </th>
          </tr>
        </thead>
        <tbody>
          {judges.map((judge) => (
            <tr key={judge.judge_id}>
              <td style={{ border: "1px solid black", padding: "8px" }}>
                {judge.judgeName}
              </td>
              <td style={{ border: "1px solid black", padding: "8px" }}>
                {judge.judgeWeightage}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}

export default function DisplayEvent() {
  const { id } = useParams();

  const [item, setItem] = useState();

  function fetchData() {
    fetch("http://localhost:8080/event?event_id=" + id)
      .then((response) => response.json())
      .then((data) => setItem(data));
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      fetchData();
    }, 1000);
    return () => clearInterval(intervalId);
  }, []);

  if (!item) return <p>Loading....</p>;

  if (item.event.performing === true)
    return (
      <>
        <h1>{item.event.eventName} Event</h1>
        <small>{item.event.eventStatus}</small>
        <p />
        {item.performers
          .filter(
            (performer) => performer.performerId === item.event.performer_id
          )
          .map((item) => (
            <h3>{item.performerName} performing.......</h3>
          ))}
        <p />
        <p />
        <DisplayJudges judges={item.judges} />
        <p />
        <DisplayPerformers performers={item.performers} />
      </>
    );

  return (
    <>
      <h1>{item.event.eventName} Event</h1>
      <small>{item.event.eventStatus}</small>
      <p />
      {item.event.eventStatus === "Event ended!!" ? null : (
        <h3>Judges Scoring.....!!</h3>
      )}
      <DisplayJudges judges={item.judges} />
      <p />
      <DisplayPerformers performers={item.performers} />
    </>
  );
}
