import React, { useEffect, useState, useParams } from "react";

export default function DisplayCategoryForm({
  categories,
  eid,
  pid,
  jid,
  weight,
}) {
  const [catValues, setCatValues] = useState([]);
  const [message, setMessage] = useState("");
  console.log(categories);

  const categoryChange = (index, e) => {
    //console.log(e);
    const values = [...catValues];
    values[index] = e.target.value;
    setCatValues(values);
  };

  const formSubmit = (e) => {
    e.preventDefault();
    setMessage("Please wait while the other judges score!!!");
    let total = 0;
    catValues.map((val) => {
      total = total + val;
    });
    total = total * weight;
    const data = {
      score: total,
      event_id: eid,
      performer_id: pid,
      judge_id: jid,
    };
    fetch("http://localhost:8080/judge/score", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  };
  const options = [1, 2, 3, 4, 5];

  return (
    <>
      <div className="container mt-5">
        <form onSubmit={formSubmit}>
          {categories.map((cat, idx) => (
            <div className="form-group">
              <label key={idx} htmlFor="dropdown">
                {cat.categoryName}
              </label>
              <select
                name="category_name"
                className="form-control"
                value={catValues[idx]}
                onChange={(e) => categoryChange(idx, e)}
              >
                <option value="">--select a score--</option>
                {options.map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
              <p />
            </div>
          ))}
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
        <h2>{message}</h2>
      </div>
    </>
  );
}
