import React, { useState } from "react";

const DynamicForm = () => {
  const [judges, setJudges] = useState([{ name: "", email: "", weightage: 1 }]);
  const [performers, setPerformers] = useState([{ name: "", score: 0 }]);

  const performerChange = (index, event) => {
    const values = [...performers];
    values[index][event.target.name] = event.target.value;
    setPerformers(values);
  };

  const performerAddField = () => {
    setPerformers([...performers, { name: "", score: 0 }]);
  };

  const performerRemoveField = (index) => {
    const values = [...performers];
    values.splice(index, 1);
    setPerofrmers(values);
  };

  const judgeChange = (index, event) => {
    const values = [...judges];
    values[index][event.target.name] = event.target.value;
    setJudges(values);
  };

  const judgeAddField = () => {
    setJudges([...judges, { name: "", email: "", weightage: 1 }]);
  };

  const judgeRemoveField = (index) => {
    const values = [...judges];
    values.splice(index, 1);
    setJudges(values);
  };

  const formSubmit = (event) => {
    event.preventDefault();
    // console.log(fields);
  };

  return (
    <div className="container mt-5">
      <form onSubmit={formSubmit}>
        <h2>Event</h2>
        <div className="col">
          <input
            type="text"
            name="name"
            className="form-control"
            placeholder="Event_Name"
            value={field.name}
            onChange={(event) => performerChange(index, event)}
            required
          />
        </div>
        <h2>Performers</h2>
        {performers.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(event) => performerChange(index, event)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(event) => performerChange(index, event)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => performerRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={performerAddField}
        >
          Add Performer
        </button>
        <p />

        <h2>Judges</h2>
        {judges.map((field, index) => (
          <div className="row mb-3" key={index}>
            <div className="col">
              <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Name"
                value={field.name}
                onChange={(event) => judgeChange(index, event)}
                required
              />
            </div>
            <div className="col">
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Email"
                value={field.email}
                onChange={(event) => judgeChange(index, event)}
                required
              />
            </div>
            <div className="col-auto">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => judgeRemoveField(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button
          type="button"
          className="btn btn-primary"
          onClick={judgeAddField}
        >
          Add Judge
        </button>

        <p />
        <button type="submit" className="btn btn-success mt-3">
          Submit
        </button>
      </form>
    </div>
  );
};

export default DynamicForm;
