import React, { useEffect, useState, useParams } from "react";

export default function ScoringPageComponent() {
  const { id } = useParams();
  const [item, setItem] = useState();
  function fetchData() {
    fetch("http://localhost:8080/event?event_id=" + id)
      .then((response) => response.json())
      .then((data) => setItem(data));
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      let s = "Event ended!";
      fetchData();
      if (item & (s.localeCompare(item.event.eventStatus) === 0)) {
        clearInterval(intervalId);
      }
    }, 1000);
  }, []);

  if (item.event.performers === -1)
    return <h2>Event will start in a few minutes! Be ready to score.</h2>;
  if (item.event.performing === false)
    return (
      <h2>
        {item.event.performers.find(
          (obj) => obj[performer_id === item.event.performer_id]
        )}
      </h2>
    );
  return <div>ScoringPageComponent</div>;
}
